INSERT INTO zirospace_services_en (id, slug, title, subtitle, content_html, excerpt, image_url, image_alt, meta_title, meta_description, keywords, is_published, order_index, created_at, updated_at) VALUES
('1', 'service-1', 'Web Development', 'Building modern and responsive websites', '<p>We specialize in creating custom web solutions tailored to your business needs. From simple landing pages to complex web applications, we use the latest technologies to deliver high-quality results.</p><p>Our services include front-end development, back-end development, API integrations, and content management systems.</p>', 'Expert web development services.', 'https://picsum.photos/id/1015/800/600', 'Web Development Image', 'Web Development Services | Zirospace', 'Get professional web development services from Zirospace.', '["web development", "website design", "frontend", "backend", "CMS"]', true, 0, NOW(), NOW()),
('2', 'service-2', 'Mobile App Development', 'Creating intuitive and powerful mobile applications', '<p>Bring your ideas to life with our mobile app development services. We build native and cross-platform applications for iOS and Android, focusing on user experience and performance.</p><p>Whether you need a consumer-facing app or an enterprise solution, we have the expertise to deliver.</p>', 'Innovative mobile app solutions.', 'https://picsum.photos/id/1025/800/600', 'Mobile App Development Image', 'Mobile App Development | Zirospace', 'Develop your next mobile app with Zirospace.', '["mobile development", "iOS", "Android", "react native", "flutter"]', true, 1, NOW(), NOW()),
('3', 'service-3', 'Cloud Solutions', 'Scalable and secure cloud infrastructure', '<p>Leverage the power of the cloud with our comprehensive cloud solutions. We help you design, deploy, and manage your infrastructure on leading cloud platforms like AWS, Azure, and Google Cloud.</p><p>Our services cover cloud migration, infrastructure as code, serverless computing, and cloud security.</p>', 'Cloud infrastructure and management.', 'https://picsum.photos/id/1035/800/600', 'Cloud Solutions Image', 'Cloud Solutions | Zirospace', 'Migrate and manage your infrastructure in the cloud.', '["cloud computing", "AWS", "Azure", "Google Cloud", "serverless"]', true, 2, NOW(), NOW()),
('4', 'service-4', 'Data Analytics', 'Turning data into actionable insights', '<p>Unlock the value of your data with our data analytics services. We help you collect, process, and analyze data to gain valuable insights and make data-driven decisions.</p><p>Our expertise includes data warehousing, ETL pipelines, business intelligence, and machine learning.</p>', 'Data analysis and insights.', 'https://picsum.photos/id/1045/800/600', 'Data Analytics Image', 'Data Analytics Services | Zirospace', 'Get actionable insights from your data with Zirospace.', '["data analytics", "business intelligence", "ETL", "data warehousing", "machine learning"]', true, 3, NOW(), NOW()),
('5', 'service-5', 'UI/UX Design', 'Designing user-centered digital experiences', '<p>Create engaging and intuitive digital products with our UI/UX design services. We focus on understanding your users and designing interfaces that are both aesthetically pleasing and easy to use.</p><p>Our process includes user research, wireframing, prototyping, and usability testing.</p>', 'User interface and experience design.', 'https://picsum.photos/id/1055/800/600', 'UI/UX Design Image', 'UI/UX Design | Zirospace', 'Design user-friendly digital products with Zirospace.', '["UI design", "UX design", "user research", "prototyping", "usability testing"]', true, 4, NOW(), NOW()),
